# Fixes up the reveal.js presentation generated by Pandoc to use our custom
# Reveal.js settings..

jsRevealInit = %{
      Reveal.initialize({
        // Push each slide change to the browser history
        history: true,

        // Optional reveal.js plugins
        dependencies: [
          { src: './lib/js/classList.js', condition: function() { return !document.body.classList; } },
          { src: './plugin/zoom-js/zoom.js', async: true },
          { src: './plugin/notes/notes.js', async: true }
        ]
      });
}

newJsRevealInit = <<TEXTMARK
      
      Reveal.initialize({
        history: true,
        transition: 'none',
        center: false,
        dependencies: [
          { src: './lib/js/classList.js', condition: function() { return !document.body.classList; } },
          { src: './plugin/zoom-js/zoom.js', async: true },
          { src: './plugin/notes/notes.js', async: true }
        ]
      });
TEXTMARK

cssLoad = %{
  <link rel="stylesheet" href="./css/theme/serif.css" id="theme">
}
newCssLoad = <<TEXTMARK

  <link rel="stylesheet" href="./css/theme/serif.css" id="theme">
  <style>
    .reveal h1 {
      font-size: 2em;
    }
    
  .reveal section img {
    box-shadow: none;
    background-color: transparent;
    border: none;
  }</style>
TEXTMARK

filename = ARGV[0]
h = File.read(filename)
h.gsub!(/\t/, '  ')
h.sub!(jsRevealInit, newJsRevealInit)
h.sub!(cssLoad, newCssLoad)

File.open(filename, "w") {|f| f.write(h)}
